// --- Enums and Basic Types ---
export type Role = 'Teacher' | 'Program Co-ordinator' | 'University' | 'Admin' | 'Department';
export type College = 'CUIET' | 'CCP' | 'CBS';
export type CourseStatus = 'Active' | 'Completed' | 'Future';
export type StudentStatus = 'Active' | 'Inactive';

// --- System-wide Settings ---
export interface SystemSettings {
  defaultCoTarget: number;
  defaultAttainmentLevels: {
    level3: number;
    level2: number;
    level1: number;
  };
  defaultWeights: {
    direct: number;
    indirect: number;
  };
}


// --- Data Structures ---
export interface User {
  id: string;
  employeeId: string;
  username: string;
  password?: string;
  role: Role;
  name: string;
  // For Program Co-ordinators: The ID of the single program they manage
  programId?: string;
  // For Teachers: ID of the Program Co-ordinator who manages them
  programCoordinatorIds?: string[];
  status?: 'Active' | 'Inactive';
  // For Department: The ID of the college they manage
  collegeId?: College;
  // For Program Co-ordinators: ID of the Department Head who manages them
  departmentId?: string;
}

export interface Program {
  id: string;
  name: string;
  collegeId: College;
}

export interface Course {
  id: string;
  name: string;
  code: string;
  programId: string;
  target: number;
  internalWeightage: number;
  externalWeightage: number;
  attainmentLevels: {
    level3: number;
    level2: number;
    level1: number;
  };
  status: CourseStatus;
  // ID of the teacher assigned to the course (default)
  teacherId?: string | null;
  // Section-specific teacher assignments
  sectionTeacherIds?: { [sectionId: string]: string };
}

export interface Student {
  id: string;
  name: string;
  programId: string;
  status: StudentStatus;
  sectionId?: string | null;
}

export interface Enrollment {
  courseId: string;
  studentId: string;
  sectionId?: string | null;
}

export interface Section {
  id: string;
  name: string;
  programId: string;
  batch: string;
}

export interface CourseOutcome {
  id: string;
  courseId: string;
  number: string;
  description: string;
}

export interface ProgramOutcome {
  id: string;
  number: string;
  description: string;
  programId: string;
}

export interface CoPoMapping {
  courseId: string;
  coId: string;
  poId: string;
  level: number;
}

export interface AssessmentQuestion {
  q: string;
  coIds: string[];
  maxMarks: number;
}

export interface Assessment {
  id: string;
  sectionId: string;
  name: string;
  type: 'Internal' | 'External';
  questions: AssessmentQuestion[];
}

export interface MarkScore {
  q: string;
  marks: number;
}

export interface Mark {
  studentId: string;
  assessmentId: string;
  scores: MarkScore[];
}

// FIX: Added CoPoMap type for use in mapping matrix.
export interface CoPoMap {
  [coId: string]: {
    [poId: string]: number;
  };
}

// FIX: Added StudentMark type for use in assessment details.
export interface StudentMark {
  studentId: string;
  [key: string]: string | number | 'U';
}


export interface AppData {
  users: User[];
  colleges: { id: College; name: string }[];
  programs: Program[];
  courses: Course[];
  students: Student[];
  enrollments: Enrollment[];
  sections: Section[];
  courseOutcomes: CourseOutcome[];
  programOutcomes: ProgramOutcome[];
  coPoMapping: CoPoMapping[];
  assessments: Assessment[];
  marks: Mark[];
  settings: SystemSettings;
}

