import React, { useState, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import { useAppContext } from '../hooks/useAppContext';
import ManageCourseOutcomes from '../components/ManageCourseOutcomes';
import ManageCourseAssessments from '../components/ManageCourseAssessments';
import CoPoMappingMatrix from '../components/CoPoMappingMatrix';
import CourseOverviewTab from '../components/CourseOverviewTab';
import StudentCOAttainmentReport from './StudentCOAttainmentReport';
import CourseFacultyAssignment from '../components/CourseFacultyAssignment';
import { Course } from '../types';

type Tab = 'Overview' | 'COs' | 'Assessments' | 'CO-PO Mapping' | 'Student Reports' | 'Faculty Assignment';

const CourseDetail: React.FC = () => {
  const { courseId } = useParams<{ courseId: string }>();
  const { data, currentUser } = useAppContext();
  const [activeTab, setActiveTab] = useState<Tab>('Overview');

  const course = useMemo(() => data.courses.find(c => c.id === courseId), [courseId, data.courses]);

  if (currentUser?.role === 'Teacher' && course && course.teacherId !== currentUser.id) {
    return (
        <div className="text-center p-8 bg-white rounded-lg shadow-md">
            <h1 className="text-2xl font-bold text-red-600">Access Denied</h1>
            <p className="text-gray-500 mt-2">You are not assigned to this course.</p>
        </div>
    );
  }

  if (!course) {
    return <div className="text-center text-red-500 p-8">Course not found.</div>;
  }

  const isCoordinator = currentUser?.role === 'Program Co-ordinator';

  const tabs: Tab[] = ['Overview', 'COs', 'Assessments', 'CO-PO Mapping'];

  if (isCoordinator) {
    tabs.push('Faculty Assignment');
  }

  tabs.push('Student Reports');


  const renderContent = () => {
    switch (activeTab) {
      case 'Overview':
        return <CourseOverviewTab course={course} />;
      case 'COs':
        return <ManageCourseOutcomes />;
      case 'Assessments':
        return <ManageCourseAssessments course={course} />;
      case 'CO-PO Mapping':
        return <CoPoMappingMatrix />;
      case 'Faculty Assignment':
        return isCoordinator ? <CourseFacultyAssignment course={course} /> : null;
      case 'Student Reports':
        return <StudentCOAttainmentReport />;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">{course.name} ({course.code})</h1>
          <p className="text-gray-500">Manage course details, outcomes, and assessments.</p>
        </div>
      </div>

      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8" aria-label="Tabs">
          {tabs.map(tab => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`${ 
                activeTab === tab
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm transition-colors`}
            >
              {tab}
            </button>
          ))}
        </nav>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md min-h-[400px]">
        {renderContent()}
      </div>
    </div>
  );
};


export default CourseDetail;

components/ManageCourseOutcomes.tsx:
import React, { useState, useMemo, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { CourseOutcome } from '../types';
import { useAppContext } from '../hooks/useAppContext';
import ExcelUploader from './ExcelUploader';
import SaveBar from './SaveBar';

const ManageCourseOutcomes: React.FC = () => {
  const { courseId } = useParams<{ courseId: string }>();
  const { data, setData, currentUser } = useAppContext();

  const [draftOutcomes, setDraftOutcomes] = useState<CourseOutcome[]>([]);
  const [initialOutcomes, setInitialOutcomes] = useState<CourseOutcome[]>([]);

  useEffect(() => {
    if (!courseId) return;
    const outcomesForCourse = data.courseOutcomes.filter(co => co.courseId === courseId);
    setDraftOutcomes(outcomesForCourse);
    setInitialOutcomes(outcomesForCourse);
  }, [data.courseOutcomes, courseId]);

  const isDirty = useMemo(() => JSON.stringify(draftOutcomes) !== JSON.stringify(initialOutcomes), [draftOutcomes, initialOutcomes]);

  const nextCoNumber = useMemo(() => {
    const highestNum = draftOutcomes.reduce((max, co) => {
      const num = parseInt(co.number.replace('CO', ''), 10);
      return !isNaN(num) && num > max ? num : max;
    }, 0);
    return `CO${highestNum + 1}`;
  }, [draftOutcomes]);

  const [newCoNumber, setNewCoNumber] = useState(nextCoNumber);
  const [newCoDescription, setNewCoDescription] = useState('');

  useEffect(() => {
    setNewCoNumber(nextCoNumber);
  }, [nextCoNumber]);

  const [editingCoId, setEditingCoId] = useState<string | null>(null);
  const [editingText, setEditingText] = useState({ number: '', description: '' });

  const canManage = currentUser?.role === 'Teacher' || currentUser?.role === 'Program Co-ordinator';

  const handleExcelUpload = (uploadedData: { code: string; description: string }[]) => {
    if (!courseId) return;
    const newOutcomes: CourseOutcome[] = uploadedData
      .filter(row => row.code && row.description)
      .map((row, i) => ({
        id: `co_excel_${Date.now()}_${i}`,
        courseId: courseId,
        number: row.code,
        description: row.description
      }));

    setDraftOutcomes(prev => [...prev, ...newOutcomes]);
    alert(`${newOutcomes.length} COs staged for upload. Click 'Save Changes' to commit.`);
  };

  const handleAddCo = () => {
    if (!newCoDescription.trim() || !courseId) {
      alert("Please fill in the Description.");
      return;
    }
    const newCo: CourseOutcome = {
      id: `co_manual_${Date.now()}`,
      courseId,
      number: newCoNumber.trim(),
      description: newCoDescription.trim()
    };

    setDraftOutcomes(prev => [...prev, newCo]);
    setNewCoDescription('');
  };

  const handleDeleteCo = (coId: string) => {
    if (window.confirm("Are you sure you want to remove this Course Outcome? This change will be saved when you click 'Save Changes'.")) {
      setDraftOutcomes(prev => prev.filter(co => co.id !== coId));
    }
  };

  const handleEditStart = (co: CourseOutcome) => {
    setEditingCoId(co.id);
    setEditingText({ number: co.number, description: co.description });
  };

  const handleEditCancel = () => {
    setEditingCoId(null);
    setEditingText({ number: '', description: '' });
  };

  const handleEditSave = () => {
    if (!editingCoId || !editingText.number.trim() || !editingText.description.trim()) return;

    setDraftOutcomes(prev => prev.map(co =>
      co.id === editingCoId
        ? { ...co, number: editingText.number.trim(), description: editingText.description.trim() }
        : co
    ));

    handleEditCancel();
  };

  const handleSave = () => {
    if (!courseId) return;
    setData(prev => ({
      ...prev,
      courseOutcomes: [
        ...prev.courseOutcomes.filter(co => co.courseId !== courseId),
        ...draftOutcomes
      ]
    }));
    setInitialOutcomes(draftOutcomes);
    alert("Course Outcomes saved successfully!");
  };

  const handleCancel = () => {
    setDraftOutcomes(initialOutcomes);
    setEditingCoId(null);
  };

  return (
    <div className="space-y-6 pb-20">
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold text-gray-700">Manage Course Outcomes (COs)</h2>
        {canManage && (
          <ExcelUploader<{ code: string; description: string }>
            onFileUpload={handleExcelUpload}
            label="Upload COs"
            format="columns: code, description"
          />
        )}
      </div>

      <div className="overflow-x-auto border border-gray-200 rounded-lg">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-1/6">CO Code</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</th>
              {canManage && <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider w-1/6">Actions</th>}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {draftOutcomes.map(co => (
              <tr key={co.id}>
                {editingCoId === co.id ? (
                  <>
                    <td className="px-6 py-4">
                      <input
                        type="text"
                        value={editingText.number}
                        onChange={(e) => setEditingText({ ...editingText, number: e.target.value })}
                        className="w-full p-2 border border-gray-300 rounded-md"
                      />
                    </td>
                    <td className="px-6 py-4">
                      <input
                        type="text"
                        value={editingText.description}
                        onChange={(e) => setEditingText({ ...editingText, description: e.target.value })}
                        className="w-full p-2 border border-gray-300 rounded-md"
                      />
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button onClick={handleEditSave} className="text-green-600 hover:text-green-800 mr-4">Apply</button>
                      <button onClick={handleEditCancel} className="text-gray-600 hover:text-gray-800">Cancel</button>
                    </td>
                  </>
                ) : (
                  <>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{co.number}</td>
                    <td className="px-6 py-4 whitespace-normal text-sm text-gray-600">{co.description}</td>
                    {canManage && (
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <button onClick={() => handleEditStart(co)} className="text-indigo-600 hover:text-indigo-800 mr-4">Edit</button>
                        <button onClick={() => handleDeleteCo(co.id)} className="text-red-600 hover:text-red-800">Delete</button>
                      </td>
                    )}
                  </>
                )}
              </tr>
            ))}
            {canManage && editingCoId === null && (
              <tr className="bg-gray-50/50">
                <td className="px-6 py-4">
                  <input
                    type="text"
                    value={newCoNumber}
                    readOnly
                    className="w-full p-2 border border-gray-300 rounded-md bg-gray-100 text-gray-500 cursor-not-allowed focus:ring-0 focus:border-gray-300"
                  />
                </td>
                <td className="px-6 py-4">
                  <input
                    type="text"
                    placeholder="Description Input"
                    value={newCoDescription}
                    onChange={(e) => setNewCoDescription(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-md bg-white text-gray-900 placeholder-gray-400 focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </td>
                <td className="px-6 py-4 text-right">
                  <button onClick={handleAddCo} className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded-lg">
                    Add
                  </button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      <SaveBar isDirty={isDirty} onSave={handleSave} onCancel={handleCancel} />
    </div>
  );
};

export default ManageCourseOutcomes;

components/ManageCourseAssessments.tsx:
import React, { useState, useMemo, useEffect } from 'react';
import { Course } from '../types';
import { useAppContext } from '../hooks/useAppContext';
import AssessmentDetails from './AssessmentDetails';
import CreateAssessmentModal from './CreateAssessmentModal';

interface ManageCourseAssessmentsProps {
  course: Course;
}

const ManageCourseAssessments: React.FC<ManageCourseAssessmentsProps> = ({ course }) => {
  const { data, setData, currentUser } = useAppContext();
  const [selectedAssessmentId, setSelectedAssessmentId] = useState<string | null>(null);
  const [selectedSectionId, setSelectedSectionId] = useState<string | null>(null);
  const [isCreateModalOpen, setCreateModalOpen] = useState(false);

  const isPC = currentUser?.role === 'Program Co-ordinator' || currentUser?.role === 'Admin';

  const sectionsForDropdown = useMemo(() => {
    // Find all sections that have students enrolled in this course
    const enrolledSectionIds = new Set(data.enrollments.filter(e => e.courseId === course.id && e.sectionId).map(e => e.sectionId));
    const allCourseSections = data.sections.filter(s => enrolledSectionIds.has(s.id));

    if (isPC) {
        return allCourseSections;
    }

    if (!currentUser || currentUser.role !== 'Teacher') return [];

    // Find sections explicitly assigned to this teacher for this course
    const teacherSectionIds = Object.entries(course.sectionTeacherIds || {})
        .filter(([, teacherId]) => teacherId === currentUser.id)
        .map(([sectionId]) => sectionId);

    if (teacherSectionIds.length > 0) {
        // If teacher has specific section assignments, show only those
        return allCourseSections.filter(s => teacherSectionIds.includes(s.id));
    }

    // If no specific section assignments, but is the main course teacher, show all sections
    if (course.teacherId === currentUser.id) {
        return allCourseSections; 
    }

    // Otherwise, teacher has no access
    return [];
  }, [data.enrollments, data.sections, course, currentUser, isPC]);

  // Effect to auto-select the first section if none is selected
  useEffect(() => {
    if (sectionsForDropdown.length > 0 && !sectionsForDropdown.some(s => s.id === selectedSectionId)) {
        setSelectedSectionId(sectionsForDropdown[0].id);
    } else if (sectionsForDropdown.length === 0) {
        setSelectedSectionId(null);
    }
  }, [sectionsForDropdown, selectedSectionId]);

  // When section changes, go back to the list view from the details view
  useEffect(() => {
    setSelectedAssessmentId(null);
  }, [selectedSectionId]);

  const assessments = useMemo(() => {
    if (!selectedSectionId) return [];
    return data.assessments.filter(a => a.sectionId === selectedSectionId);
  }, [data.assessments, selectedSectionId]);

  const handleDeleteAssessment = (assessmentId: string) => {
    if (window.confirm("Are you sure you want to delete this assessment and all its questions and marks? This action cannot be undone.")) {
      setData(prev => ({
        ...prev,
        assessments: prev.assessments.filter(a => a.id !== assessmentId),
        marks: prev.marks.filter(m => m.assessmentId !== assessmentId)
      }));
    }
  }

  const sectionName = sectionsForDropdown.find(s => s.id === selectedSectionId)?.name || '';

  // This dropdown will be visible in both list and detail view for PCs
  const SectionSelector = (
    <div className="mb-6 pb-4 border-b">
        <label htmlFor="section-select" className="block text-sm font-medium text-gray-700">Select Section</label>
        <select
            id="section-select"
            value={selectedSectionId || ''}
            onChange={(e) => setSelectedSectionId(e.target.value)}
            disabled={sectionsForDropdown.length === 0}
            className="mt-1 block w-full max-w-sm pl-3 pr-10 py-2 text-base bg-white text-gray-900 border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md disabled:bg-gray-100"
        >
            {sectionsForDropdown.map(section => (
                <option key={section.id} value={section.id}>Section {section.name}</option>
            ))}
        </select>
        {sectionsForDropdown.length === 0 && <p className="mt-2 text-sm text-gray-500">You are not assigned to any sections for this course, or no sections have been created.</p>}
    </div>
  );

  // If an assessment is selected, show details. The section selector will be shown above for PCs.
  if (selectedAssessmentId) {
    return (
        <div className="space-y-6">
            {isPC && SectionSelector}
            <AssessmentDetails 
                assessmentId={selectedAssessmentId} 
                onBack={() => setSelectedAssessmentId(null)}
                course={course} // Pass the course down
            />
        </div>
    );
  }

  // Otherwise, show the list of assessments for the selected section.
  return (
    <div className="space-y-6">
      {SectionSelector}
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold text-gray-700">
            {selectedSectionId ? `Assessments for Section ${sectionName}` : 'Select a section to view assessments'}
        </h2>
        {(isPC || sectionsForDropdown.length > 0) && (
          <button 
            onClick={() => setCreateModalOpen(true)} 
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded-lg disabled:bg-indigo-300 disabled:cursor-not-allowed"
            disabled={!selectedSectionId}
            title={!selectedSectionId ? "Please select a section first" : "Create a new assessment"}
          >
            Create Assessment
          </button>
        )}
      </div>

      <div className="space-y-4">
        {assessments.map(assessment => {
          const totalMaxMarks = assessment.questions.reduce((sum, q) => sum + q.maxMarks, 0);
          const hasMarks = data.marks.some(m => m.assessmentId === assessment.id);
          return (
            <div key={assessment.id} className="bg-gray-50 p-4 rounded-lg flex justify-between items-center border border-gray-200">
              <div>
                <p className="font-semibold text-gray-800 flex items-center">
                  {assessment.name}
                  {hasMarks && (
                    <span className="ml-2 text-xs font-semibold bg-green-100 text-green-800 px-2 py-0.5 rounded-full">
                       Marks Uploaded
                    </span>
                  )}
                </p>
                <p className="text-sm text-gray-500">{assessment.type} | Max Marks: {totalMaxMarks}</p>
              </div>
              <div className="space-x-4">
                <button 
                  onClick={() => setSelectedAssessmentId(assessment.id)}
                  className="text-indigo-600 hover:text-indigo-800 font-semibold"
                >
                  Manage Questions
                </button>
                {isPC && (
                   <button onClick={() => handleDeleteAssessment(assessment.id)} className="text-red-600 hover:text-red-800 font-semibold">
                      Delete
                  </button>
                )}
              </div>
            </div>
          )
        })}
        {assessments.length === 0 && selectedSectionId && <p className="text-gray-500 text-center py-4">No assessments found for this section.</p>}
      </div>
      {isCreateModalOpen && selectedSectionId && (
        <CreateAssessmentModal sectionId={selectedSectionId} onClose={() => setCreateModalOpen(false)} />
      )}
    </div>
  );
};

export default ManageCourseAssessments;

